CREATE TABLE `magnalister_ebay_categories` ( `CategoryID` bigint(11) NOT NULL DEFAULT '0', `SiteID` int(4) NOT NULL DEFAULT '77', `CategoryName` varchar(128) NOT NULL DEFAULT '', `CategoryLevel` int(3) NOT NULL DEFAULT '1', `ParentID` bigint(11) NOT NULL DEFAULT '0', `LeafCategory` tinyint(4) NOT NULL DEFAULT '1', `StoreCategory` tinyint(4) NOT NULL DEFAULT '0', `Attributes` int(11) NOT NULL, `Expires` datetime NOT NULL, PRIMARY KEY (`CategoryID`,`SiteID`,`StoreCategory`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8